CREATE TABLE jet_stops
(
  id integer,
  iata_faa character varying(20),
  longitude double precision,
  latitude double precision,
  x double precision,
  y double precision
);



CREATE TABLE jet_orders (
    pid integer,
    did integer,
    d_airport character varying(20),
    a_airport character varying(20),
    p_x double precision,
    p_y double precision,
    d_x double precision,
    d_y double precision,
    num_passengers integer,
    opentime integer,
    closetime double precision,
    servicetime integer
);


